    // ]);

    setTextBox("");
  }

  return (
    <form onSubmit={handleSubmit} className="new-item-form">
      <div className="form-row">
        <label>New item</label>
        <inputx
          type="text"
          id="item"
          value={textBox}
          onChange={(e) => setTextBox(e.target.value)}
        />
      </div>
      <button className="btn">Add</button>
    </form>
  );
}
