import { useState } from "react";

export default function App() {
  const [textBox, setTextbox] = useState("");
  const [todos, setTodos] = useState([]);

  console.log(todos);

  function handleSubmit(e) {
    e.preventDefault();

    setTodos([
      ...todos,
      { title: textBox, id: crypto.randomUUID(), completed: false },
    ]);
    setTextbox("");
  }

  function handleDelete(id) {
    setTodos(todos.filter((todo) => todo.id != id));
  }

  function toggleTodo(id, completed) {
    setTodos((currentTodo) => {
      return currentTodo.map((todo) => {
        if (todo.id === id) {
          return { ...todo, completed };
        }
      });
    });
  }

  /// TOGGLE CHECK BOX

  /// we add checked property on the input value
  /// this will get the value from the object we are rendering out by submitting the form
  /// by default this will be false

  /// we then need to listen out for that value of false to change

  /// we do this by adding onChange, taking the event, and running toggleTodo on it
  /// passing in the individual elements todo.id (random element key number)
  /// and putting an e.target on this value

  return (
    <>
      <form onSubmit={handleSubmit} className="new-item-form">
        <div className="form-row">
          <label htmlFor="item">New Item</label>
          <input
            type="text"
            id="item"
            value={textBox}
            onChange={(e) => setTextbox(e.target.value)}
          />
        </div>

        <button className="btn">Add</button>
      </form>

      <h1 className="header">Todo List</h1>
      <ul className="list">
        {todos.map((todo) => {
          return (
            <li key={todo.id}>
              <label>
                <input
                  type="checkbox"
                  checked={todo.completed}
                  onChange={(e) => toggleTodo(todo.id, e.target.checked)}
                />
                {todo.title}
              </label>
              <button
                onClick={() => handleDelete(todo.id)}
                className="btn btn-danger"
              >
                Delete
              </button>
            </li>
          );
        })}
      </ul>
    </>
  );
}
