import { useState } from "react";

export default function Board() {
  const [square, setSquare] = useState(Array(9).fill(null));
  const [xIsNext, setxIsNext] = useState(true);

  console.log(square);

  function clicked(i) {
    const nextSquare = square.slice();

    if (xIsNext) {
      nextSquare[i] = "X";
    } else {
      nextSquare[i] = "O";
    }
    setSquare(nextSquare);
  }

  return (
    <>
      <div className="board-row">
        <Square value={square[0]} clicked={() => clicked(0)} />
        <Square value={square[1]} clicked={() => clicked(1)} />
        <Square value={square[2]} clicked={() => clicked(2)} />
      </div>

      <div className="board-row">
        <Square value={square[3]} clicked={() => clicked(3)} />
        <Square value={square[4]} clicked={() => clicked(4)} />
        <Square value={square[5]} clicked={() => clicked(5)} />
      </div>

      <div className="board-row">
        <Square value={square[6]} clicked={() => clicked(6)} />
        <Square value={square[7]} clicked={() => clicked(7)} />
        <Square value={square[8]} clicked={() => clicked(8)} />
      </div>
    </>
  );
}

function Square({ clicked, value }) {
  return (
    <button className="square" onClick={clicked}>
      {value}
    </button>
  );
}
