import { useState } from "react";

export default function App() {
  const [newItem, setNewItem] = useState("");
  const [todos, SetTodos] = useState([]);

  function handleSubmit(e) {
    e.preventDefault();

    SetTodos((currentTodos) => {
      return [
        ...currentTodos,
        { id: crypto.randomUUID(), title: newItem, completed: false },
      ];
    });

    setNewItem("");
  }

  function toggleTodo(id, completed) {
    SetTodos((currentTodos) => {
      return currentTodos.map((todo) => {
        if (todo.id === id) {
          return { ...todo, completed };
        }

        return todo;
      });
    });
  }

  //// taking the currentTodos - and looping mover them as currentTodos
  /// the returning the currentTodos, mapping over them, calling each of them todo
  /// then if the todo.id of the original objects is the same as the id we take in
  /// then spread the current arrays inside the todo array, and replace completed with the updated completed

  return (
    <>
      <form onSubmit={handleSubmit} className="new-item-form">
        <div className="form-row">
          <label>New item</label>
          <input
            type="text"
            id="item"
            value={newItem}
            onChange={(e) => setNewItem(e.target.value)}
          />
        </div>
        <button className="btn">Add</button>
      </form>

      <h1 className="header">Todo list</h1>
      <ul className="list">
        {todos.map((todo) => {
          return (
            <li key={todo.id}>
              <label>
                <input
                  type="checkbox"
                  checked={todo.checked}
                  onChange={(e) => toggleTodo(todo.id, e.target.checked)}
                />

                {todo.title}
              </label>
            </li>
          );
        })}

        <li>
          <label>
            <input type="checkbox" />
            item 1
          </label>
        </li>
      </ul>
    </>
  );
}
