import { useState, useEffect } from "react";

export default function App() {
  const [textItem, setTextItem] = useState("");
  const [savedItem, setSavedItem] = useState([]);
  const [pokemonList, setPokemonList] = useState(null);
  const [error, setError] = useState(""); // Error state for displaying errors

  console.log(
    pokemonList?.held_items?.[0]
      ? pokemonList.held_items[0].item.name
      : "not available"
  );

  function handleSubmit(e) {
    e.preventDefault();
    if (!textItem.trim()) {
      setError("Please enter a valid Pokémon name.");
      return;
    }
    setSavedItem([...savedItem, { title: textItem.toLowerCase() }]);
    setTextItem("");
    setError(""); // Clear any previous errors
  }

  useEffect(() => {
    const fetchData = async () => {
      if (savedItem.length > 0) {
        const pokemonName = savedItem[savedItem.length - 1];

        try {
          const response = await fetch(
            `https://pokeapi.co/api/v2/pokemon/${pokemonName.title}`
          );

          if (!response.ok) {
            throw new Error("Pokémon not found.");
          }

          const data = await response.json();
          setPokemonList(data);
          setError(""); // Clear any previous errors
        } catch (err) {
          setError(err.message); // Set error message
          setPokemonList(null); // Clear Pokémon data on error
        }
      }
    };
    fetchData();
  }, [savedItem]);

  return (
    <>
      <form onSubmit={handleSubmit}>
        <input
          type="text"
          value={textItem}
          onChange={(e) => setTextItem(e.target.value)}
          placeholder="Enter Pokémon name"
        />
        <button>Search</button>
      </form>

      {/* Display error message */}
      {error && <p style={{ color: "red", marginTop: "10px" }}>{error}</p>}

      {/* Display Pokémon details if available */}
      {pokemonList && (
        <div className="whole-card">
          <h3>Basic Pokemon</h3>
          <div className="top-section">
            <h1>{pokemonList.name}</h1>
            <h2>180 HP</h2>
          </div>

          <div className="pokemon-pic">
            <img
              src={pokemonList?.sprites?.front_default || "No image available"}
              alt="pokemon pic"
            />
          </div>

          <div className="quick-attack">
            <p>
              <span>
                {pokemonList?.held_items?.[0]
                  ? pokemonList.held_items[0].item.name
                  : "not available"}
              </span>
              : Flip a coin. if heads this attack does 50 more damage
            </p>
            <p className="attack">70+</p>
          </div>

          <div className="ability">
            <p>
              <span>
                {pokemonList?.held_items?.[1]
                  ? pokemonList.held_items[1].item.name
                  : "not available"}
              </span>
              : if there is a stadium card in play, discard it. if you do, this
              attack does 100 more damage
            </p>
            <p className="attack">70+</p>
          </div>

          <div className="bottom-section">
            <p>weakness</p>
            <p>resistance</p>
            <p>retreat cost</p>
          </div>
        </div>
      )}
    </>
  );
}
