import { useState } from "react";

export default function App() {
  const [textbox, setTextbox] = useState("");
  const [state, setState] = useState([]);

  return (
    <>
      <Header
        textbox={textbox}
        setTextbox={setTextbox}
        state={state}
        setState={setState}
      />
      <Section state={state} setState={setState} />
    </>
  );
}

function Header({ textbox, setTextbox, state, setState }) {
  function handleSubmit(e) {
    e.preventDefault();
    setState([...state, { title: textbox, id: crypto.randomUUID() }]);
  }

  function handleDelete() {
    console.log(state);
  }

  return (
    <div className="Box">
      <div>
        <h2 className="top-text">New Item</h2>
        <form onSubmit={handleSubmit} className="form">
          <input
            className="input"
            type="text"
            value={textbox}
            onChange={(e) => setTextbox(e.target.value)}
          />
          <button onClick={() => handleDelete()} className="button-add">
            Add
          </button>
        </form>
      </div>
    </div>
  );
}

function Section({ state, setState }) {
  console.log(state);

  return (
    <div className="section-box">
      <div>
        <h2 className="todo-header">Todo List</h2>
        <label className="check-list">
          {state.map((todo) => {
            return (
              <ul key={todo.id}>
                <li className="todo-list">
                  <input type="checkbox" />
                  <label>{todo.title}</label>
                  <button className="button-delete">Delete</button>
                </li>
              </ul>
            );
          })}
        </label>
      </div>
    </div>
  );
}
