import { useState } from "react";

export default function App() {
  const [textbox, setTextbox] = useState("");
  const [state, setState] = useState([]);

  return (
    <>
      <Header textbox={textbox} setTextbox={setTextbox} />
      <Section state={state} setState={setState} />
    </>
  );
}

function Header({ textbox, setTextbox, state, setState }) {
  // const [textbox, setTextbox] = useState("");
  // const [state, setState] = useState([]);

  function handleSubmit(e) {
    setState([...state, { title: textbox, id: crypto.randomUUID() }]);
    e.preventDefault();
  }

  console.log(state);

  return (
    <div className="Box">
      <div>
        <h2 className="top-text">New Item</h2>
        <form onSubmit={handleSubmit} className="form">
          <input
            className="input"
            type="text"
            value={textbox}
            onChange={(e) => setTextbox(e.target.value)}
          />
          <button className="button-add">Add</button>
        </form>
      </div>
    </div>
  );
}

function Section() {
  return (
    <div className="section-box">
      <div>
        <h2 className="todo-header">Todo List</h2>
        <label className="check-list">
          <input type="checkbox" />
          hi
        </label>
        <button className="button-delete">Delete</button>
      </div>
    </div>
  );
}
