import { useState } from "react";

export default function Board() {
  const [square, setSquare] = useState(Array(9).fill(null));

  function clicked(i) {
    const nextSquare = square.slice();
    nextSquare[i] = "X";
    console.log(nextSquare);
  }

  return (
    <>
      <div className="board-row">
        <Square value={square[0]} clicked={() => clicked(0)} />
        <Square value={square[1]} clicked={() => clicked(1)} />
        <Square value={square[2]} clicked={clicked} />
      </div>
    </>
  );
}

function Square({ clicked }) {
  return <button className="square" onClick={clicked}></button>;
}
